mapper.select:
select
dep_id, name, address, status
from ssm.department


        and dep_id = #{depId}


        and name = #{name}


        and address = #{address}


        and status = #{status}

mapper.update:
UPDATE ssm.department
SET status=0
WHERE dep_id = #{depId}

UPDATE ssm.department
SET status=1
WHERE dep_id = #{depId}
